// UserProfile.js

import React, { useState, useEffect } from 'react';
import {
  Typography,
  CircularProgress,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Paper,
  Grid,
  Card,
  CardContent,
} from '@mui/material';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import './UserProfile.css';

const UserProfile = () => {
  const [data, setData] = useState([]);
  const [stats, setStats] = useState(null);
  const [loadingData, setLoadingData] = useState(true);
  const [loadingStats, setLoadingStats] = useState(true);
  const username = 'testuser'; // Replace with actual username

  useEffect(() => {
    // Fetch user data
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:5000/data/${username}`);
        if (response.ok) {
          const result = await response.json();
          setData(result.data);
        } else {
          console.error('Error fetching data:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoadingData(false);
      }
    };

    // Fetch user stats
    const fetchStats = async () => {
      try {
        const response = await fetch(`http://localhost:5000/data/stats/${username}`);
        if (response.ok) {
          const result = await response.json();
          setStats(result.stats);
        } else {
          console.error('Error fetching stats:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching stats:', error);
      } finally {
        setLoadingStats(false);
      }
    };

    fetchData();
    fetchStats();
  }, [username]);

  // Prepare data for the chart (last 24 hours)
  const chartData = data
    .filter((item) => {
      const itemDate = new Date(item.timestamp);
      const now = new Date();
      return now - itemDate <= 24 * 60 * 60 * 1000; // Last 24 hours
    })
    .map((item) => ({
      time: new Date(item.timestamp).toLocaleTimeString(),
      angle: item.angle,
      rotation: item.rotation,
    }));

  return (
    <div className="user-profile-container">
      <Typography variant="h4" className="user-profile-title">
        Patient Profile: {username}
      </Typography>

      {loadingData || loadingStats ? (
        <div className="user-profile-loading">
          <CircularProgress />
          <Typography className="user-profile-loading-text">Loading data...</Typography>
        </div>
      ) : (
        <Grid container spacing={4} justifyContent="center">
          {/* Data Table */}
          <Grid item xs={12} md={10}>
            <Paper elevation={3} className="user-profile-paper">
              <Typography variant="h6" className="user-profile-subtitle">
                Data Records
              </Typography>
              {data.length > 0 ? (
                <Table size="small" className="user-profile-table">
                  <TableHead>
                    <TableRow>
                      <TableCell className="table-header-cell">Timestamp</TableCell>
                      <TableCell align="right" className="table-header-cell">
                        Angle
                      </TableCell>
                      <TableCell align="right" className="table-header-cell">
                        Rotation
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {data.map((item, index) => (
                      <TableRow key={index}>
                        <TableCell className="table-cell">
                          {new Date(item.timestamp).toLocaleString()}
                        </TableCell>
                        <TableCell align="right" className="table-cell">
                          {item.angle.toFixed(2)}
                        </TableCell>
                        <TableCell align="right" className="table-cell">
                          {item.rotation.toFixed(2)}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              ) : (
                <Typography className="no-data-text">No data available</Typography>
              )}
            </Paper>
          </Grid>

          {/* Statistics */}
          <Grid item xs={12} md={10}>
            <Paper elevation={3} className="user-profile-paper">
              <Typography variant="h6" className="user-profile-subtitle">
                Statistics
              </Typography>
              {stats ? (
                <Grid container spacing={2} justifyContent="center">
                  {['angle', 'rotation'].map((param) => (
                    <Grid item xs={12} sm={6} md={4} key={param}>
                      <Card className="user-profile-card">
                        <CardContent className="user-profile-card-content">
                          <Typography className="user-profile-card-title">
                            {param.toUpperCase()}
                          </Typography>
                          <Typography className="user-profile-card-text">
                            Mean: {stats[param].mean.toFixed(2)}
                          </Typography>
                          <Typography className="user-profile-card-text">
                            Std Dev: {stats[param].std.toFixed(2)}
                          </Typography>
                          <Typography className="user-profile-card-text">
                            Min: {stats[param].min.toFixed(2)}
                          </Typography>
                          <Typography className="user-profile-card-text">
                            Max: {stats[param].max.toFixed(2)}
                          </Typography>
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              ) : (
                <Typography className="no-data-text">No statistics available</Typography>
              )}
            </Paper>
          </Grid>

          {/* Chart */}
          <Grid item xs={12} md={10}>
            <Paper elevation={3} className="user-profile-paper user-profile-chart">
              <Typography variant="h6" className="user-profile-subtitle">
                Last 24 Hours Data
              </Typography>
              {chartData.length > 0 ? (
                <div className="recharts-wrapper">
                  <ResponsiveContainer width="100%" height={400}>
                    <LineChart data={chartData}>
                      <XAxis dataKey="time" className="chart-axis" />
                      <YAxis className="chart-axis" />
                      <Tooltip wrapperClassName="chart-tooltip" />
                      <Legend wrapperClassName="chart-legend" />
                      <Line
                        type="monotone"
                        dataKey="angle"
                        className="chart-line-angle"
                        dot={false}
                      />
                      <Line
                        type="monotone"
                        dataKey="rotation"
                        className="chart-line-rotation"
                        dot={false}
                      />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              ) : (
                <Typography className="no-data-text">
                  No data available for the last 24 hours
                </Typography>
              )}
            </Paper>
          </Grid>
        </Grid>
      )}
    </div>
  );
};

export default UserProfile;
