import React from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginScreen.css'; // Ensure this path is correct based on your file structure
import logo from '../assets/KneeKare_Pro_Logo.jpg'; // Update this path to where you store your logo

const LoginScreen = () => {
  const navigate = useNavigate();

  const handleUserLogin = () => {
    navigate('/user-profile');
  };

  const handleClinicianLogin = () => {
    navigate('/clinician-profile');
  };

  return (
    <div className="login-container">
      <img src={logo} alt="KneeKare Pro Logo" className="logo" />
      <div className="login-section">
        <h2>User Login</h2>
        <button className="login-button" onClick={handleUserLogin}>Login as User</button>
      </div>
      <div className="login-section">
        <h2>Clinician Login</h2>
        <button className="login-button" onClick={handleClinicianLogin}>Login as Clinician</button>
      </div>
    </div>
  );
};

export default LoginScreen;
