import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginScreen.css';
import logo from '../assets/KneeKare_Pro_Logo.jpg'; // Make sure the path to your logo is correct

const LoginScreen = () => {
  const navigate = useNavigate();
  const [credentials, setCredentials] = useState({ username: '', password: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prevCredentials => ({
      ...prevCredentials,
      [name]: value
    }));
  };

  const handleLogin = (profilePath) => {
    // For now, we're not validating credentials
    navigate(profilePath);
  };

  return (
    <div className="login-container">
      <img src={logo} alt="KneeKare Pro Logo" className="logo" />
      <div className="login-section">
        <h2>User Login</h2>
        <input 
          type="text" 
          placeholder="Username" 
          name="username" 
          value={credentials.username} 
          onChange={handleInputChange} 
          className="login-input" 
        />
        <input 
          type="password" 
          placeholder="Password" 
          name="password" 
          value={credentials.password} 
          onChange={handleInputChange} 
          className="login-input" 
        />
        <button className="login-button" onClick={() => handleLogin('/user-profile')}>Login as User</button>
      </div>
      <div className="login-section">
        <h2>Clinician Login</h2>
        <input 
          type="text" 
          placeholder="Username" 
          name="username" 
          value={credentials.username} 
          onChange={handleInputChange} 
          className="login-input" 
        />
        <input 
          type="password" 
          placeholder="Password" 
          name="password" 
          value={credentials.password} 
          onChange={handleInputChange} 
          className="login-input" 
        />
        <button className="login-button" onClick={() => handleLogin('/clinician-profile')}>Login as Clinician</button>
      </div>
    </div>
  );
};

export default LoginScreen;
